function setElementMiddle(e, t, n) {
    var r = n;
    if (r) {
        var i = r.height();
        var s = r.width();
        //if (i < e) {
        //    r.css("margin-top", Math.floor((e - i) / 2) + "px")
        //}
        if (s < t) {
            r.css("margin-left", Math.floor((t - s) / 2) + "px")
        }
    }
}


function resizeImg(el, maxW, maxH) {
    var oEl = el;
    var maxWidth = maxW;
    var maxHeight = maxH;
    oEl.css("width", "auto").css("height", "auto");
    oEl.removeAttr("width").removeAttr("height");
    var width = oEl.width();
    var height = oEl.height();

    if (width > maxWidth && height <= maxHeight) {
        //console.log('maxW');
        var ratio = maxWidth / width;
        oEl.css("width", maxWidth);
        oEl.css("height", height * ratio);

    } else if (width <= maxWidth && height > maxHeight) {
        //console.log('maxH');
        var ratio = maxHeight / height;
        oEl.css("width", width * ratio);
        oEl.css("height", maxHeight);

    } else if (width > maxWidth && height > maxHeight) {
        //console.log('maxW & H');
        if (width > height) {
            //console.log('w > h');
            var ratio = maxWidth / width;
            oEl.css("width", maxWidth);
            oEl.css("height", height * ratio);
        } else if (height > width) {
            //console.log('h > w');
            var ratio = maxHeight / height;
            oEl.css("width", width * ratio);
            oEl.css("height", maxHeight);

        } else {
            //console.log('h = w');
            oEl.css("width", maxWidth);
            oEl.css("height", maxHeight);
        }

    } else {
        oEl.css("width", width);
        oEl.css("height", height);
    }
}